mapper.update:
update login
set Name = #{name,jdbcType=VARCHAR},
  LoginIP = #{loginip,jdbcType=VARCHAR},
  LoginBrowser = #{loginbrowser,jdbcType=VARCHAR},
  OfflineTime = #{offlinetime,jdbcType=CHAR}
where SystemNo = #{systemno,jdbcType=VARCHAR}

mapper.select:
select SystemNo, Name, LoginIP, LoginBrowser, OfflineTime
from login
where SystemNo = #{systemno,jdbcType=VARCHAR}
