mapper.update:
update role
set State = #{state,jdbcType=VARCHAR},
  Name = #{name,jdbcType=VARCHAR},
  Flag = #{flag,jdbcType=VARCHAR},
  Description = #{description,jdbcType=VARCHAR}
where Id = #{id,jdbcType=INTEGER}

mapper.select:
select Id, State, Name, Flag, Description
from role
where Id = #{id,jdbcType=INTEGER}
